import React from 'react';
import { useSelector } from 'react-redux';
import './App.css';
import { selectUser } from "./features/userSlice";
import HactuallyChat from "./HactuallyChat";

function App() {

  const user = useSelector(selectUser);


  return (
    <div className="app">
      {user ? (
        <HactuallyChat />
      ): (
        <h2>You need to login</h2>
      )}
      
    </div>
  );
}

export default App;
